fragment KEY : [a-zA-Z_][a-zA-Z0-9_]*;
fragment DIGITS : [0-9]+;
fragment EXP : [eE] [+-]? DIGITS;

STRING
    :  '\'' ( '\'\'' | '\\' . | ~['\\\r\n] )* '\''
    |  '0' X ([0-9a-fA-F] | '\\' [ \t]* [\r\n])+  
    |  '0' B ([01] | '\\' [ \t]* [\r\n])+
