if self.text.upper() in KEYWORDS:
    self.type = MyLexer.KEYWORD
      }
    ;

KEYWORD : ; 

NUMBER
    : DIGITS ('.' DIGITS)? (EXP)?
    | '.' DIGITS (EXP)?
    ;

OPERATOR
    : '+' | '-' | '*' | '/' | '%'
    | '=' | '<>' | '!=' | '<' | '>' | '<=' | '>='
    | '+=' | '-=' | '*=' | '/='
    | '&' | '|' | '^' | '~' | '<<' | '>>'
    | '.' | ';' | ',' | '(' | ')'
    ;


mode COMMENT_MODE;

COMMENT_MODE_RULE
    : '/*' { self._commentLevel += 1; }
    | '*/' {
        self._commentLevel -= 1;
        if self._commentLevel == 0:
            self.popMode()
      }
    | .
    ;
